export default {
	artist: searchArtist.text ?? selectArtist.selectedOptionLabel,

	mainSearch: () => {
		searchAnyArtist.run(async() => { 
			await fetchAlbums.run()
				.then(() => { 
				this.processArtist(searchAnyArtist.data.artists.items[0].id)}) }, 
												() => showAlert('Unable to fetch artist ID','warning'))
	},


	fetchArtistAlbums: () => {
		const albums = fetchAlbums.data.items.map((item) => ({
			label:item.name , value: item.id
		}))
		return _.filter(albums)
	},

		fetchArtistTracks: () => {
			let tracks = fetchAlbumTracks.data.items.map((item) => ({
				label: item.name, value:item.external_urls.spotify
			}))
			return _.uniq(tracks)
		},

			seudh: () => { return fetchAlbumTracks.data},

				//-----Custom search artists on Spotify-----//
				runCustomSearch: async () => {
					const searchValue = searchArtist.text ?? selectArtist.selectedOptionLabel;
					return searchValue
				},

					processArtist: (artistCode) => {
						storeValue('artistCode', artistCode)
							.then(() => {
							fetchTopTracks.run(() => {
								youTubeCustomSearch.run();
								showAlert('♬ ♩ 𝅘𝅥𝅰 ♩ 𝅘𝅥𝅰 ♬')}, 
																 () => showAlert('Uh uh! :( Looks like auth token has expired','warning') )
						})
					}
}
