() => {
  spotifySearch.run(() => {
    storeValue('artistId', spotifySearch.data.albums.items[0].artists[0].id);
    fetchAlbums.run().then(() => {
      SpotifyManager.fetchArtistTopTracks(spotifySearch.data.artists.items[0].id);
    });
  }, () => showAlert('Unable to fetch artist ID', 'warning'));
}

