() => {
  return requestToken.run().then(response => {
    console.log("STEP 3: *REQUEST CALL* Successfully ran requestToken query ");
    let tokens = [storeValue('authToken', response.access_token), storeValue('refreshToken', response.refresh_token)];
    return Promise.all(tokens).then(() => {
      console.log("Auth code in store is " + appsmith.store.authToken);
      console.log("Refresh token in store is " + appsmith.store.refreshToken);
    }).catch(err => {
      console.log("Could not store tokens in store");
      return showAlert('Could not store tokens in store ' + err.toString());
    });
  });
}

